    if(isset($_POST['message'])){
        if(!empty($_POST['floating_first_name']) && !empty($_POST['floating_last_name']) && !empty($_POST['floating_email']) && !empty($_POST['floating_password']) && !empty($_POST['repeat_password']) && !empty($_POST['floating_phone']) && !empty($_POST['floating_company']) && !empty($_POST['feedback'])){
            $fname = $_POST['floating_first_name'];
            $lname = $_POST['floating_last_name'];
            $username = $fname . $lname;
            $email = $_POST['floating_email'];
            $phone = $_POST['floating_phone'];
            $feedback = $_POST['feedback'];

            $connect = new mysqli('localhost','root','','my--store');
            $result = $connect->query("INSERT INTO `feedback`(`id`, `username`, `email`, `phone`, `feedback`) VALUES (null,'$username','$email','$phone','$feedback')");
            if($result == true){
                echo"
                    <script>
                    Swal.fire({
                        position: 'center',
                        icon: 'success',
                        title: 'SENDING SUCCESSFUL!',
                        showConfirmButton: false,
                        timer: 1500
                    })
                    </script>
                ";
            }
        }
